import {
	container,
	coloredShadow,
	title,
	cardTitle,
	description,
	mlAuto,
	infoColor,
	roseColor,
} from '../../material-kit';

import imageStyles from '../Image';

import rotatingCards from './rotatingcards';

const styles = {
	container,
	coloredShadow,
	title,
	mlAuto,
	cardTitle,
	...imageStyles,
	...rotatingCards,
	sectionGray: {
		background: '#E5E5E5',
	},
	sectionWhite: {
		background: '#FFFFFF',
	},
	cardTitleAbsolute: {
		...cardTitle,
		position: 'absolute !important',
		bottom: '15px !important',
		left: '15px !important',
		color: '#fff !important',
		fontSize: '1.125rem !important',
		textShadow: '0 2px 5px rgba(33, 33, 33, 0.5) !important',
	},
	cardTitleWhite: {
		'&, & a': {
			...title,
			marginTop: '.625rem',
			marginBottom: '0',
			minHeight: 'auto',
			color: '#fff !important',
		},
	},
	cardCategory: {
		marginTop: '10px',
		'& svg': {
			position: 'relative',
			top: '8px',
		},
	},
	cardCategorySocial: {
		marginTop: '10px',
		'& .fab,& .fas,& .far,& .fal,& .material-icons': {
			fontSize: '22px',
			position: 'relative',
			marginTop: '-4px',
			top: '2px',
			marginRight: '5px',
		},
		'& svg': {
			position: 'relative',
			top: '5px',
		},
	},
	cardCategorySocialWhite: {
		marginTop: '10px',
		color: 'rgba(255, 255, 255, 0.8)',
		'& .fab,& .fas,& .far,& .fal,& .material-icons': {
			fontSize: '22px',
			position: 'relative',
			marginTop: '-4px',
			top: '2px',
			marginRight: '5px',
		},
		'& svg': {
			position: 'relative',
			top: '5px',
		},
	},
	cardCategoryWhite: {
		marginTop: '10px',
		color: 'rgba(255, 255, 255, 0.7)',
	},
	cardCategoryFullWhite: {
		marginTop: '10px',
		color: '#FFFFFF',
	},
	cardDescription: {
		...description,
	},
	cardDescriptionWhite: {
		color: 'rgba(255, 255, 255, 0.8)',
	},
	author: {
		display: 'inline-flex',
		'& a': {
			color: '#3C4858',
		},
	},
	authorWhite: {
		display: 'inline-flex',
		'& a': {
			color: 'rgba(255, 255, 255, 0.8)',
		},
	},
	avatar: {
		width: '30px',
		height: '30px',
		overflow: 'hidden',
		borderRadius: '50%',
		marginRight: '5px',
	},
	statsWhite: {
		color: 'rgba(255, 255, 255, 0.8)',
		display: 'inline-flex',
		'& .fab,& .fas,& .far,& .fal,& .material-icons': {
			position: 'relative',
			top: '3px',
			marginRight: '3px',
			marginLeft: '3px',
			fontSize: '18px',
			lineHeight: '18px',
		},
		'& svg': {
			position: 'relative',
			top: '3px',
			marginRight: '3px',
			marginLeft: '3px',
			width: '18px',
			height: '18px',
		},
	},
	stats: {
		color: '#999999',
		display: 'flex',
		'& .fab,& .fas,& .far,& .fal,& .material-icons': {
			position: 'relative',
			top: '3px',
			marginRight: '3px',
			marginLeft: '3px',
			fontSize: '18px',
			lineHeight: '18px',
		},
		'& svg': {
			position: 'relative',
			top: '3px',
			marginRight: '3px',
			marginLeft: '3px',
			width: '18px',
			height: '18px',
		},
	},
	justifyContentCenter: {
		WebkitBoxPack: 'center !important',
		MsFlexPack: 'center !important',
		justifyContent: 'center !important',
	},
	iconWrapper: {
		color: 'rgba(255, 255, 255, 0.76)',
		margin: '10px auto 0',
		width: '130px',
		height: '130px',
		border: '1px solid #E5E5E5',
		borderRadius: '50%',
		lineHeight: '174px',
		'& .fab,& .fas,& .far,& .fal,& .material-icons': {
			fontSize: '55px',
			lineHeight: '55px',
		},
		'& svg': {
			width: '55px',
			height: '55px',
		},
	},
	iconWrapperColor: {
		borderColor: 'rgba(255, 255, 255, 0.25)',
	},
	iconWhite: {
		color: '#FFFFFF',
	},
	iconRose: {
		color: roseColor,
	},
	iconInfo: {
		color: infoColor,
	},
	marginTop30: {
		marginTop: '30px',
	},
	textCenter: {
		textAlign: 'center',
	},
	marginBottom20: {
		marginBottom: '20px',
	},
};

export default styles;
