import { primaryColor, dangerColor, successColor, defaultFont } from '../../material-kit';

const customInputStyle = {
	disabled: {
		'&:before': {
			backgroundColor: 'transparent !important',
		},
	},
	underline: {
		'&:hover:not($disabled):before,&:before': {
			borderBottomColor: '#D2D2D2 !important',
			borderBottomWidth: '1px !important',
		},
		'&:after': {
			borderBottomColor: primaryColor,
		},
	},
	underlineError: {
		'&:after': {
			borderBottomColor: dangerColor,
		},
	},
	underlineSuccess: {
		'&:after': {
			borderBottomColor: successColor,
		},
	},
	labelRoot: {
		...defaultFont,
		color: '#AAAAAA !important',
		fontWeight: '400',
		fontSize: '14px',
		lineHeight: '1.42857',
		top: '10px',
		'& + $underline': {
			marginTop: '0px',
		},
	},
	labelRootError: {
		color: dangerColor + ' !important',
	},
	labelRootSuccess: {
		color: successColor + ' !important',
	},
	feedback: {
		position: 'absolute',
		bottom: '4px',
		right: '0',
		zIndex: '2',
		display: 'block',
		width: '24px',
		height: '24px',
		textAlign: 'center',
		pointerEvents: 'none',
	},
	formControl: {
		margin: '0 0 17px 0',
		paddingTop: '27px',
		position: 'relative',
		'& svg,& .fab,& .far,& .fal,& .fas,& .material-icons': {
			color: '#495057',
		},
	},
	whiteUnderline: {
		'&:hover:not($disabled):before,&:before': {
			borderBottomColor: '#FFFFFF',
		},
		'&:after': {
			borderBottomColor: '#FFFFFF',
		},
	},
	input: {
		color: '#495057',
		'&,&::placeholder': {
			fontSize: '14px',
			fontFamily: '"Roboto", "Helvetica", "Arial", sans-serif',
			fontWeight: '400',
			lineHeight: '1.42857',
			opacity: '1',
		},
		'&::placeholder': {
			color: '#AAAAAA',
		},
	},
	whiteInput: {
		'&,&::placeholder': {
			color: '#FFFFFF',
			opacity: '1',
		},
	},
};

export default customInputStyle;
